import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HttpClientModule } from '@angular/common/http';
import { UserService } from './services/user.service';

interface User {
  name: {
    first: string;
    last: string;
  };
  email: string;
  picture: {
    medium: string;
    large: string;
  };
  location: {
    city: string;
    state: string;
    country: string;
  };
  phone: string;
}

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, HttpClientModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  user: User | null = null;
  loading: boolean = false;
  error: string | null = null;

  constructor(private userService: UserService) {}

  ngOnInit(): void {}

  fetchRandomUser(): void {
    this.loading = true;
    this.error = null;
    
    this.userService.getRandomUser().subscribe({
      next: (data: any) => {
        this.user = data.results[0];
        this.loading = false;
      },
      error: (err) => {
        console.error('Error fetching user:', err);
        this.error = 'Failed to fetch user. Please try again.';
        this.loading = false;
      }
    });
  }
}
<div class="container">
  <div class="header">
    <h1>Random User Generator</h1>
    <button (click)="fetchRandomUser()" [disabled]="loading" class="fetch-btn">
      {{ loading ? 'Loading...' : 'Fetch Random User' }}
    </button>
  </div>

  <div *ngIf="error" class="error-message">
    {{ error }}
  </div>

  <div *ngIf="user" class="user-card">
    <div class="user-image">
      <img [src]="user.picture.large" [alt]="user.name.first + ' ' + user.name.last">
    </div>
    
    <div class="user-info">
      <h2>{{ user.name.first }} {{ user.name.last }}</h2>
      
      <div class="info-group">
        <label>Email:</label>
        <p>{{ user.email }}</p>
      </div>
    </div>
  </div>

  <div *ngIf="!user && !loading && !error" class="empty-state">
    <p>Click the button above to fetch a random user!</p>
  </div>
</div>
